      const newOtpFields = [...otpFields];
      newOtpFields[index] = value;
      setOtpFields(newOtpFields);
      setUserPass(newOtpFields.join(''));
    } else if (value === '') { // If value is empty, remove digit
      const newOtpFields = [...otpFields];
      newOtpFields[index] = '';
      setOtpFields(newOtpFields);
      setUserPass(newOtpFields.join(''));
    }
  };

